    return dehumidifierObject ? dehumidifierObject.value : null;
}

// Finden des Dehumidifier-Wertes
const dehumidifierValue = findDehumidifierValue(sensors);

// Ausgabe des Dehumidifier-Wertes zur Überprüfung
// node.warn(`Dehumidifier-Wert: ${dehumidifierValue}`);

// Optional: Speichern des Dehumidifier-Wertes in msg zur weiteren Verwendung
msg.payload = dehumidifierValue;

// Rückgabe der msg zur Weiterverarbeitung im Flow
return msg;
