// Funktion zur berprfung der Gleichheit von Werten mit einer Dezimalstelle fr numerische Werte
function areEqual(value1, value2) {
    // Versuche beide Werte in Zahlen umzuwandeln
    const num1 = parseFloat(value1);
    const num2 = parseFloat(value2);

    // Wenn beide Werte Zahlen sind, vergleiche sie als Zahlen mit einer Dezimalstelle
    if (!isNaN(num1) && !isNaN(num2)) {
        return num1.toFixed(1) === num2.toFixed(1);
    }

    // Vergleiche die Werte als Strings, wenn sie keine Zahlen sind
    return value1 === value2;
}

// Funktion zur Aktualisierung des state-Werts aus livedata
function updateState(data, livedata) {
    const entityId = livedata.entity_id;

    for (const key in data) {
        if (data.hasOwnProperty(key) && typeof data[key] === 'object' && data[key] !== null) {
            // Suchen Sie in data[key] nach dem entsprechenden Wert
            for (const subKey in data[key]) {
                if (data[key].hasOwnProperty(subKey)) {
                    // berprfen, ob der aktuelle Zustand anders ist als der neue Zustand
                    if (subKey === entityId) {
                        console.log(`Current state: ${data[key][subKey]}, New state: ${livedata.state}`);
                        if (!areEqual(data[key][subKey], livedata.state)) {
                            console.log('State is different. Updating state...');
                            data[key][subKey] = livedata.state;
                            changed = true; // Markiere als gendert
                        } else {
                            console.log('State is the same. No update needed.');
                        }
                        return; // Beendet die Schleife nach der ersten relevanten nderung
                    }
                }
            }
        }
    }
}

