function calculateRelativeHumidity(T, Td) {
    // Magnus-Formel Konstanten
    const a = 17.27;
    const b = 237.7;

    // Berechnung des Sttigungsdampfdrucks (SDP) bei der aktuellen Temperatur
    const saturationVaporPressure = 6.1078 * Math.exp((a * T) / (b + T));

    // Berechnung des Sttigungsdampfdrucks (SDP) beim Taupunkt
    const dewPointVaporPressure = 6.1078 * Math.exp((a * Td) / (b + Td));

    // Berechnung der relativen Feuchtigkeit
    const RH = 100 * (dewPointVaporPressure / saturationVaporPressure);

    return Math.round(RH * 100) / 100; // Rundung auf zwei Dezimalstellen
}

