function calculateVPD(luftTemperatur, dewPoint) {

    // Stellen Sie sicher, dass alle Eingaben Zahlen sind
    luftTemperatur = parseFloat(luftTemperatur);
    dewPoint = parseFloat(dewPoint);

    // berprfen Sie, ob einer der Werte NaN ist
    if (isNaN(luftTemperatur) || isNaN(dewPoint)) {
        return "unavailable";
    }

    // Berechnung des Sttigungsdampfdrucks (SDP) fr Lufttemperatur
    let sdpLuft = 0.6108 * Math.exp((17.27 * luftTemperatur) / (luftTemperatur + 237.3));
    // Berechnung des tatschlichen Dampfdrucks (ADP) am Taupunkt
    let adp = 0.6108 * Math.exp((17.27 * dewPoint) / (dewPoint + 237.3));

    // Berechnung des VPD
    let vpd = sdpLuft - adp;


    return vpd.toFixed(2);  // Rundet den VPD-Wert auf zwei Dezimalstellen
}

