function calculateVaporPressure(dewPoint) {
    // Ensure input is a number
    dewPoint = parseFloat(dewPoint);

    // Check for NaN values
    if (isNaN(dewPoint)) {
        return "unavailable";
    }

    // Calculate vapor pressure using the formula (in mb)
    let vaporPressure_mb = 6.11 * Math.pow(10, (7.5 * dewPoint) / (237.3 + dewPoint));

    return vaporPressure_mb.toFixed(2);  // Round the vapor pressure to two decimal places
}

function calculateSaturationVaporPressure(temperature) {
    // Ensure input is a number
    temperature = parseFloat(temperature);

    // Check for NaN values
    if (isNaN(temperature)) {
        return "unavailable";
    }

    // Calculate saturation vapor pressure using the formula (in mb)
    let saturationVaporPressure_mb = 6.11 * Math.pow(10, (7.5 * temperature) / (237.3 + temperature));

    return saturationVaporPressure_mb.toFixed(2);  // Round the vapor pressure to two decimal places
}

