// Funktion zur Berechnung des Durchschnitts
const AvgTemp = (temps) => {
    let sum = 0;
    let count = 0;

    temps.forEach((entry) => {
        const temp = parseFloat(entry.value);
        if (!isNaN(temp)) {
            sum += temp;
            count++;
        }
    });

    if (count === 0) {
        return "unavailable";
    }

    const avg = sum / count;
    return avg.toFixed(2);
};

