const ppfd = (msg.payload / maxPower) * maxPPFD;

// PPFD auf zwei Nachkommastellen runden
const roundedPPFD = parseFloat(ppfd.toFixed(2));

// Berechnung von DLI
const secondsPerHour = 3600;
const hoursPerDay = 12; // Anzahl der Stunden pro Tag, die das Licht an ist
let dli = roundedPPFD * secondsPerHour * hoursPerDay / 1e6;

