let relativeLuftfeuchtigkeit = parseFloat(msg.payload.AvgData.AvgHumData); // Stellen Sie sicher, dass dies eine Zahl ist

let vpd = calculateVPD(luftTemperatur, blattTemperatur, relativeLuftfeuchtigkeit);

