const tolerance = 0.00; // Toleranzwert fr die VPD-Anpassung

// Der aktuelle VPD-Wert wird aus dem Payload geholt und in eine Zahl umgewandelt.
const currentVPD = parseFloat(msg.payload.currentVPD);

// Ziel-VPD-Bereich wird aus dem Payload geholt.
const targetVPDRange = msg.payload.targetVPD;

// Logging der aktuellen VPD und Ziel-VPD-Bereich zur berprfung und Debugging.
node.warn(`Veggi-VPD array between: ${targetVPDRange[0]} and ${targetVPDRange[1]}`);

// Berechnung der prozentualen Abweichung vom Ziel-VPD und Rundung auf ganze Zahlen
const percentDifference = Math.round(currentVPD < targetVPDRange[0] 
    ? ((currentVPD - targetVPDRange[0]) / targetVPDRange[0]) * 100 
    : ((currentVPD - targetVPDRange[1]) / targetVPDRange[1]) * 100);

// Funktion zur Berechnung der notwendigen Anpassungen basierend auf der Differenz zum VPD-Zielbereich.
function calculateAdjustment(currentVPD, targetVPDRange) {
    // Wenn der aktuelle VPD unter dem minimalen Zielbereich liegt, Anpassungen vornehmen, um VPD zu erhhen.
    if (currentVPD < targetVPDRange[0] - tolerance) {
        node.warn(`Veggi-VPD:: ${currentVPD} zu gering. Erhhe Temp oder verringere die Feuchtigkeit`);
        node.warn(`Veggi % VPD Target --> ${percentDifference} %`);
        return {
            exhaustSpeed: "increased",      // Erhht die Abluftgeschwindigkeit, um feuchte Luft herauszuziehen
            humidifier: "reduced",          // Schaltet den Luftbefeuchter aus, um Feuchtigkeit zu reduzieren
            dehumidifier: "increased",       // Schaltet den Luftentfeuchter an, um Feuchtigkeit zu reduzieren
            heaterFan: "increased",         // Schaltet den Beheizer an, um die Feuchtigkeit zu reduzieren und Temp zu erhhen
            coolerFan: "reduced",           // Schaltet den Khler an, um die Feuchtigkeit zu erhhen und Temp zu verringern
            ventsALL: "increased",          // Erhht die Geschwindigkeit der oberen Belftung, um mehr trockene Luft einzulassen
            ventsTopSpeed: "increased",     // Erhht die Geschwindigkeit der oberen Belftung, um mehr trockene Luft einzulassen
            ventsDownSpeed: "increased",    // Erhht die Geschwindigkeit der unteren Belftung
            lightIntensity: "increased"     // Erhht die Lichtintensitt, um die Temperatur zu steigern (wenn das die Temperatur beeinflusst)
        };

    }
    // Wenn der aktuelle VPD ber dem maximalen Zielbereich liegt, Anpassungen vornehmen, um VPD zu senken.
    else if (currentVPD > targetVPDRange[1] + tolerance) {
        node.warn(`Veggi-VPD: ${currentVPD} zu hoch. Verringere Temp oder erhhe die Feuchtigkeit`);
        node.warn(`Veggi % VPD Target --> ${percentDifference} %`);
        return {
            exhaustSpeed: "reduced",    // Reduziert die Abluftgeschwindigkeit, hlt feuchte Luft im Raum
            humidifier: "increased",    // Schaltet den Luftbefeuchter ein, um Feuchtigkeit zu erhhen
            dehumidifier: "reduced",    // Schaltet den Luftentfeuchter aus, um Feuchtigkeit zu erhhen
            heaterFan: "reduced",       // Schaltet den Heizer aus, um die Temperatur zu verringern
            coolerFan: "increased",     // Schaltet den Khler an, um die Temperatur zu verringern
            ventsALL: "reduced",        // Reduziert die Geschwindigkeit der Belftung, um mehr feuchte Luft zu halten
            ventsTopSpeed: "reduced",   // Reduziert die Geschwindigkeit der oberen Belftung
            ventsDownSpeed: "reduced",  // Reduziert die Geschwindigkeit der unteren Belftung
            lightIntensity: "reduced"   // Verringert die Lichtintensitt, um die Temperatur zu senken (wenn das die Temperatur beeinflusst)
        };
    }
    // Wenn der VPD im Zielbereich liegt, sind keine Anpassungen erforderlich.
    node.warn(`Veggi-VPD: ${currentVPD} im gewnschten Bereich`);
    return {
        exhaustSpeed: "unchanged",
        humidifier: "unchanged",
        dehumidifier: "unchanged",    
        heaterFan: "unchanged",        
        coolerFan: "unchanged",        
        ventsALL: "unchanged",       
        ventsTopSpeed: "unchanged",
        ventsDownSpeed: "unchanged",
        lightIntensity: "unchanged"
    };
}

