        return {
            exhaustSpeed: "increased",      // Erhht die Abluftgeschwindigkeit, um feuchte Luft herauszuziehen
            humidifier: "reduced",          // Schaltet den Luftbefeuchter aus, um Feuchtigkeit zu reduzieren
            dehumidifier: "increased",      // Schaltet den Luftentfeuchter an, um Feuchtigkeit zu reduzieren
            heaterFan: "increased",         // Schaltet den Beheizer an, um die Feuchtigkeit zu reduzieren und Temp zu erhhen
            coolerFan: "reduced",           // Schaltet den Beheizer an, um die Feuchtigkeit zu erhhen und Temp zu verringern
            ventsALL: "increased",          // Erhht die Geschwindigkeit der oberen Belftung, um mehr trockene Luft einzulassen
            ventsTopSpeed: "increased",     // Erhht die Geschwindigkeit der oberen Belftung, um mehr trockene Luft einzulassen
            ventsDownSpeed: "increased",    // Erhht die Geschwindigkeit der unteren Belftung
            lightIntensity: "increased"     // Erhht die Lichtintensitt, um die Temperatur zu steigern (wenn das die Temperatur beeinflusst)
        };
    }
    // Wenn der aktuelle VPD ber dem maximalen Zielbereich liegt, Anpassungen vornehmen, um VPD zu senken.
    else if (currentVPD > targetVPDRange + tolerance) {
        node.warn(`Flower-VPD: ${currentVPD} zu hoch. Verringere Temp oder erhhe die Feuchtigkeit.`);
        return {
            exhaustSpeed: "reduced",    // Reduziert die Abluftgeschwindigkeit, hlt feuchte Luft im Raum
            humidifier: "increased",    // Schaltet den Luftbefeuchter ein, um Feuchtigkeit zu erhhen
            dehumidifier: "reduced",    // Schaltet den Luftentfeuchter aus, um Feuchtigkeit zu erhhen
            heaterFan: "reduced",       // Schaltet den Beheizer aus, um die Feuchtigkeit zu erhhen und Temp zu senken
            coolerFan: "increased",     // Schaltet den Khler an, um die Feuchtigkeit zu erhhen und Temp zu verringern
            ventsALL: "reduced",        // Reduziert die Geschwindigkeit der Belftung, um mehr feuchte Luft zu halten
