// Der function-Node empfngt eine Nachricht mit einem Watt-Wert
// Beispiel fr die Eingangsnachricht: { payload: 120 } // das entspricht 100% Leistung

// PPF bei 100% Leistung (120 Watt)
const fullPowerPPF = 342; // PPF in Mikromol pro Sekunde (mol/s)
// Die Flche, ber die der PPF verteilt ist
const area = 0.36; // Flche in Quadratmetern (m) fr eine Flche von 60x60 cm
// Maximale Wattzahl fr die 100% Leistung
const maxWatt = 120;
// Verlustfaktor (10% Verlust)
const lossFactor = 0.9;

// PPFD berechnen basierend auf dem aktuellen Wattwert
let ppfd = (msg.payload / maxWatt) * fullPowerPPF / area;

// Bercksichtigung des Verlustes von 10%
ppfd *= lossFactor;

// PPFD auf zwei Nachkommastellen runden
const roundedPPFD = parseFloat(ppfd.toFixed(2));

// Berechnung von DLI
const secondsPerHour = 3600;
const hoursPerDay = 18; // Anzahl der Stunden pro Tag, die das Licht an ist
let dli = roundedPPFD * secondsPerHour * hoursPerDay;

// Umrechnung von Mikromol pro Sekunde in Mol pro Tag
dli /= 1e6;

// DLI auf zwei Nachkommastellen runden
dli = parseFloat(dli.toFixed(2));

// Erstellen Sie zwei separate Nachrichtenobjekte fr die Ausgnge
const msg1 = { payload: roundedPPFD }; // fr den ersten Output (PPFD), gerundet auf zwei Nachkommastellen
const msg2 = { payload: dli };         // fr den zweiten Output (DLI), gerundet auf zwei Nachkommastellen

// Rckgabe der Nachrichten in einem Array, wobei jeder Index dem entsprechenden Output entspricht
return [msg1, msg2];

