const calculateDewPoint = (temperature, humidity) => {
    const temp = parseFloat(temperature);
    const hum = parseFloat(humidity);

    if (isNaN(temp) || isNaN(hum)) {
        return "unavailable";
    }

    const dewPoint = temp - ((100 - hum) / 5);
    return parseFloat(dewPoint.toFixed(3));
};

