  color: black !important;
  border: 0.16rem solid rgba(255, 255, 255, 0.8);
  border-radius: 25px 25px 25px 25px;
  box-shadow: 2px 2px 15px #ffffff;
}
ha-card.is-on .bubble-icon-container {
  background: black;
  border: 0.12rem solid rgba(0, 0, 0, 0.7);
}
